// Fallback to English if the language is not found.
// It also handles cases like 'en-US' by defaulting to 'en'.
export function getTranslations(langCode: string): TranslationSet {
    const baseLang = langCode.split('-')[0];
    return ALL_TRANSLATIONS[langCode] || ALL_TRANSLATIONS[baseLang] || ALL_TRANSLATIONS.en;
}
